styles:
:host {
  display: block;
  position: relative;
  width: calc(50% - 50px);
  border: 1px solid black; }
  :host .timeline-item::after {
    content: '';
    position: absolute;
    width: 15px;
    height: 15px;
    right: calc(-50px);
    background: black;
    top: 40px;
    transform: translate(50%, -50%);
    border-radius: 50%;
    z-index: 5; }
  :host .timeline-item .item-arrow {
    position: absolute;
    right: -1px;
    top: 40px;
    background: white;
    height: 20px;
    width: 1px;
    transform: translateY(-50%); }
    :host .timeline-item .item-arrow::before, :host .timeline-item .item-arrow::after {
      content: '';
      position: absolute;
      background: black;
      height: 10px;
      width: 1.5px;
      left: 5px; }
    :host .timeline-item .item-arrow::before {
      top: 50%;
      transform: skew(135deg, 0deg); }
    :host .timeline-item .item-arrow::after {
      top: 0;
      transform: skew(-135deg, 0deg); }
  :host.right .timeline-item::before {
    left: 0; }
  :host.right .timeline-item::after {
    left: calc(-50px);
    transform: translate(-50%, -50%); }
  :host.right .timeline-item .item-arrow {
    left: -1px; }
    :host.right .timeline-item .item-arrow::before, :host.right .timeline-item .item-arrow::after {
      left: -5px; }
    :host.right .timeline-item .item-arrow::before {
      top: 0; }
    :host.right .timeline-item .item-arrow::after {
      top: 50%; }
  :host.right {
    left: calc(50% + 50px); }
